import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Book } from '../book';
import { BookService } from '../book.service';

@Component({
  selector: 'app-update-book',
  templateUrl: './update-book.component.html',
  styleUrls: ['./update-book.component.css']
})
export class UpdateBookComponent implements OnInit {
  uploadedImage!: File;
  id!: number;
  book: Book = new Book();
  constructor(private bookService: BookService,
    private route: ActivatedRoute,
    private router: Router) { }

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];

    this.bookService.getBookById(this.id).subscribe(data => {
      this.book = data;
    }, error => console.log(error));
  }

  onSubmit(){
    this.bookService.updateBook(this.id, this.book).subscribe( data =>{
      this.goToBookList();
    }
    , error => console.log(error));
  }

  goToBookList(){
    this.router.navigate(['/books']);
  }

  public onImageUpload(event ) {
    this.uploadedImage = event.target.files[0];
  }

}
<div class="row">
    <div class="card col-md-6 offset-md-3 offset-md-3">
        <div class="row">
            <h3 class="text-center"> Update Book </h3>
            <hr />
            <div class="card-body">
                <form (ngSubmit)="onSubmit()">

                    <div class="form-group">
                        <label>Name</label>
                        <input type="text" class="form-control" id="name" [(ngModel)]="book.name"
                            name="name">
                    </div>

                    <div class="form-group">
                        <label>price</label>
                        <input type="text" class="form-control" id="pages" [(ngModel)]="book.pages"
                            name="price">
                    </div>

                    <div class="form-group">
                        <label>price</label>
                        <input type="text" class="form-control" id="description" [(ngModel)]="book.description"
                            name="description">
                    </div>

                    <div class="form-group">
                        <label>price</label>
                        <input type="text" class="form-control" id="pages" [(ngModel)]="book.pages"
                            name="pages">
                    </div>

                    <br/>
                    <div class="form-group">
                        <img   src="{{ book.pathImage }}" > 
                        <br>
                        <label for="profileImage">Trocar Imagem</label>  
                        <input type="file" value=""  (change)="onImageUpload($event)">
                     </div>


                    <br />
                    <button class="btn btn-success" type="submit">Submit</button>

                </form>
            </div>
        </div>
    </div>
</div>
