import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router'; 
import { Book } from '../book';
import { BookService } from '../book.service';

@Component({
  selector: 'app-create-book',
  templateUrl: './create-book.component.html',
  styleUrls: ['./create-book.component.css']
})
export class CreateBookComponent  implements OnInit {
  uploadedImage!: File;
  book: Book = new Book();
  constructor(private bookService: BookService,
    private router: Router) { }

  ngOnInit(): void {
  }

  public onImageUpload(event) {
    this.uploadedImage = event.target.files[0];
  }

  saveBook(){
    this.bookService.createBook(this.book).subscribe( data =>{
      console.log(data);
      this.goToBookList();
    },
    error => console.log(error));
  }

  goToBookList(){
    this.router.navigate(['/books']);
  }

  onSubmit(){
    console.log(this.book);
    this.saveBook();
  }
}
<div class="row">
    <div class="card col-md-6 offset-md-3 offset-md-3">
        <div class="row">
            <h3 class="text-center"> Create Book </h3>
            <hr />
            <div class="card-body">
                <form (ngSubmit)="onSubmit()">

                    <div class="form-group">
                        <label>Name</label>
                        <input type="text" class="form-control" id="name" [(ngModel)]="book.name"
                            name="name">
                    </div>

                    <div class="form-group">
                        <label>price</label>
                        <input type="text" class="form-control" id="price" [(ngModel)]="book.price"
                            name="price">
                    </div>

                    <div class="form-group">
                        <label>description</label>
                        <input type="text" class="form-control" id="description" [(ngModel)]="book.description"
                            name="description">
                    </div>

                    <div class="form-group">
                        <label>pages</label>
                        <input type="text" class="form-control" id="pages" [(ngModel)]="book.pages"
                            name="pages">
                    </div>

                    <div class="form-group">

                            <label for="profileImage">Upload Image</label>  
                            <input type="file" (change)="onImageUpload($event)">
                    </div>

                    <br />
                    <button class="btn btn-success" type="submit">Submit</button>

                </form>
            </div>
        </div>
    </div>
</div>
